    setIsVideo(true); // Show the video when the user clicks
  };

  return (
    <section
      className="hero-section"
      style={{
        backgroundImage: isVideo ? "none" : "url('/assets/onxytree.png')", // Toggle background image
        backgroundSize: "cover",
        backgroundPosition: "center center",
        width: "100%",
        height: "400px",
      }}
    >
      <div className="hero-overlay">
        <h2>YEAR OVER YEAR. KNOWLEDGE SHARED. LEGACIES BUILT.</h2>
        <h3>ONYX 81</h3>
        <div className="tagline">STRONGER TOGETHER</div>
        <p>Asset Library Promo Video</p>
        <small>An extension of our community</small>
        <div className="video-length">02:05</div>
        <a href="#" className="video-link" onClick={handleVideoClick}>
          Watch Video
        </a>
      </div>

      {/* Video container, which is visible only when isVideo is true */}
      {isVideo && (
        <div className="video-container">
          <video width="100%" height="100%" controls autoPlay>
            {/* Replace with actual video source URL */}
