import React, { useState, useEffect } from "react";
import "./Hero.css";

function Hero() {
  const [isVideoPlaying, setIsVideoPlaying] = useState(false);
  const [isVideoReady, setIsVideoReady] = useState(false); // State to control video fade-in

  const toggleVideo = () => {
    setIsVideoPlaying(true);
    setIsVideoReady(true); // Once the button is clicked, we trigger fade-in
  };

  useEffect(() => {
    if (isVideoPlaying) {
      const timeout = setTimeout(() => {
        setIsVideoReady(false); // Fade the video in after a slight delay
      }, 500); // Adjust timing if needed
      return () => clearTimeout(timeout);
    }
  }, [isVideoPlaying]);

  return (
    <section
      className="hero-section"
      style={{
        backgroundImage: isVideoPlaying ? "none" : "url('/assets/onxytree.png')", // Remove background image when video is playing
        backgroundSize: "cover",
        backgroundPosition: "center center",
        height: "400px",
      }}
    >
      {/* If video is NOT playing, show the overlay with text */}
      {!isVideoPlaying && (
        <div className="hero-overlay">
          <h2>YEAR OVER YEAR. KNOWLEDGE SHARED. LEGACIES BUILT.</h2>
          <h3>ONYX 81</h3>
          <div className="tagline">STRONGER TOGETHER</div>
          <p>Asset Library Promo Video</p>
          <small>An extension of our community</small>
          <div className="video-length">02:05</div>
          <button className="video-link" onClick={toggleVideo}>
            Watch Video
          </button>
        </div>
      )}

      {/* If video is playing, hide the overlay and show the full-screen video */}
      {isVideoPlaying && (
        <div className={`video-container ${isVideoReady ? "fade-in" : ""}`}>
          <iframe
            src="https://1drv.ms/v/c/55b3d777fd08d7d0/IQS3Txg1GsYRRowCTUfnjej3AZnw5gTd41mKbKTpQvnlfa8"
            width="100%"
            height="100%"
            frameBorder="0"
            scrolling="no"
            allowFullScreen
          ></iframe>
        </div>
      )}
    </section>
  );
}

export default Hero;
