import React, { useState } from "react";
import "./Hero.css";

function Hero() {
  const [isVideoPlaying, setIsVideoPlaying] = useState(false);

  const toggleVideo = () => {
    setIsVideoPlaying(true); // Enable video playing state
  };

  return (
    <section
      className={`hero-section ${isVideoPlaying ? "video-playing" : ""}`}
      style={{
        backgroundImage: isVideoPlaying ? "none" : "url('/assets/onxytree.png')", // Remove image once video starts
        backgroundSize: "cover",
        backgroundPosition: "center center",
        height: "400px",
      }}
    >
      {/* Show the overlay only when video isn't playing */}
      {!isVideoPlaying && (
        <div className="hero-overlay">
          <h2>YEAR OVER YEAR. KNOWLEDGE SHARED. LEGACIES BUILT.</h2>
          <h3>ONYX 81</h3>
          <div className="tagline">STRONGER TOGETHER</div>
          <p>Asset Library Promo Video</p>
          <small>An extension of our community</small>
          <div className="video-length">02:05</div>
          <button className="video-link" onClick={toggleVideo}>
            Watch Video
          </button>
        </div>
      )}

      {/* Video section that appears once video is playing */}
      {isVideoPlaying && (
        <div className="video-container">
          <iframe
            src="https://1drv.ms/v/c/55b3d777fd08d7d0/IQS3Txg1GsYRRowCTUfnjej3AZnw5gTd41mKbKTpQvnlfa8?autoplay=1"  // Autoplay parameter added
            width="960"
            height="540"
            frameBorder="0"
            scrolling="no"
            allowFullScreen
            title="Asset Library Promo Video"
          ></iframe>
        </div>
      )}
    </section>
  );
}

export default Hero;
